    changeValue = (val) => {
        this.setState({orders: [
                {name: val, age: "900", id: 1},
                {name: "ZZZ", age: "200", id: 2}
            ]});
    }

    changeValueHandler = (event, id) => {
        const orderIndex = this.state.orders.findIndex(o=> { return o.id === id; });
        const order = {...this.state.orders[orderIndex]};
        order.name = event.target.value;

        const orders = [...this.state.orders];
        orders[orderIndex] = order;

        this.setState({orders: orders});
    }

    toggleListHandler = () => {
        const isShow = this.state.showList;
        this.setState({showList: !isShow});
    }
    deleteOrder = (index) => {
        //const orders = this.state.orders.slice();
        const orders = [...this.state.orders];
        orders.splice(index, 1);
        this.setState(({orders: orders}));
    }

    onLoad = () => {
        this.setState({ start: false, showCategory: false});
    }

    categoryClick = (event, guid) => {        
        let categories = this.state.apiHomePage.data.filter(function (o) { return o.guid === guid; });
        console.log(categories);
        
        this.setState(
            { start: false, showCategory: true, apiCategoryPage: { data: categories } }
        );
    }
    categoryBackClick = (event, guid) => {
        this.setState(
            { start: false, showCategory: false, apiCategoryPage: { data: null } }
        );
    }

